import{_ as e,c as t,o as i,P as o}from"./chunks/framework.kLAB-UjF.js";const u=JSON.parse('{"title":"cgroup","description":"","frontmatter":{"title":"cgroup","author":"Se7en","categories":["Interview"],"tags":["Operating System","cgroup"]},"headers":[],"relativePath":"courses/interview/操作系统/05-cgroup.md","filePath":"courses/interview/操作系统/05-cgroup.md","lastUpdated":1729736003000}'),a={name:"courses/interview/操作系统/05-cgroup.md"};function p(r,s,n,c,l,g){return i(),t("div",null,s[0]||(s[0]=[o('<h2 id="如何查看当前系统使用的是-cgroup-v1-还是-cgroup-v2" tabindex="-1">如何查看当前系统使用的是 cgroup v1 还是 cgroup v2？ <a class="header-anchor" href="#如何查看当前系统使用的是-cgroup-v1-还是-cgroup-v2" aria-label="Permalink to &quot;如何查看当前系统使用的是 cgroup v1 还是 cgroup v2？&quot;">​</a></h2><p>要查看当前系统使用的是 <strong>cgroup v1</strong> 还是 <strong>cgroup v2</strong>，可以通过以下几种方法：</p><p><strong>方法一：使用 <code>stat</code> 命令</strong></p><p>执行以下命令：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark-dimmed vp-code"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#6CB6FF;">stat</span><span style="--shiki-light:#005CC5;--shiki-dark:#6CB6FF;"> -fc</span><span style="--shiki-light:#032F62;--shiki-dark:#96D0FF;"> %T</span><span style="--shiki-light:#032F62;--shiki-dark:#96D0FF;"> /sys/fs/cgroup/</span></span></code></pre></div><ul><li>如果输出为 <code>cgroup2fs</code>，则表示系统正在使用 <strong>cgroup v2</strong>。</li><li>如果输出为 <code>tmpfs</code>，则表示系统正在使用 <strong>cgroup v1</strong>.</li></ul><p><strong>方法二：使用 <code>mount</code> 命令</strong></p><p>你也可以通过以下命令检查挂载的文件系统：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark-dimmed vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#F69D50;">mount</span><span style="--shiki-light:#D73A49;--shiki-dark:#F47067;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#F69D50;"> grep</span><span style="--shiki-light:#032F62;--shiki-dark:#96D0FF;"> cgroup</span></span></code></pre></div><ul><li>如果输出中包含 <code>cgroup2</code>，则表示系统正在使用 <strong>cgroup v2</strong>。</li><li>如果只显示 <code>cgroup</code>，则说明是 <strong>cgroup v1</strong>.</li></ul><p><strong>方法三：检查 <code>/proc/filesystems</code></strong></p><p>运行以下命令：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark-dimmed vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#F69D50;">grep</span><span style="--shiki-light:#032F62;--shiki-dark:#96D0FF;"> cgroup</span><span style="--shiki-light:#032F62;--shiki-dark:#96D0FF;"> /proc/filesystems</span></span></code></pre></div><ul><li>如果输出中包含 <code>nodev cgroup2</code>，则表示 <strong>cgroup v2</strong> 已安装。</li><li>如果只显示 <code>nodev cgroup</code>，则表示只安装了 <strong>cgroup v1</strong>.</li></ul><p>参考资料：</p><ul><li><a href="https://www.lixueduan.com/posts/linux/08-cgroup-v2/" target="_blank" rel="noreferrer">Linux Cgroup V2 初体验</a></li></ul>',16)]))}const h=e(a,[["render",p]]);export{u as __pageData,h as default};
